package alg;

public class IncreasingTriangle {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
//print any char row number of times
		// increasing triangle
		for(int i=1;i<=5;i++){
			for(int j=1;j<=i;j++){
				System.out.print(j+" ");
			}
			System.out.println();
			
		}
	}

}
