package alg;

public class PrintCharP1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//5,4,3,2,1 stars
int c=5;
		for(int i=0;i<5;i++){
			
			for (int j=0;j<c;j++){
				System.out.print("*"+" ");
			}
			c--;
			System.out.println();
				
		}
	}

}
